import { PayloadAction, createSlice } from "@reduxjs/toolkit";

type User = {
  id: string | number;
  name: string;
  lastName: string;
};

const users: User[] = [
  {
    id: 1,
    name: "John",
    lastName: "Doe",
  },
  {
    id: 2,
    name: "Jane",
    lastName: "Smith",
  },
  {
    id: 3,
    name: "Alice",
    lastName: "Johnson",
  },
  {
    id: 4,
    name: "Bob",
    lastName: "Brown",
  },
];
const UserSlice = createSlice({
  name: "User",
  initialState: users,
  reducers: {
    listUser: (state) => {
      return state;
    },

    //
    addUser: (state, action: PayloadAction<User>) => {
      //console.log("action.types", action.type);
      // console.log(action.payload);

      state.push(action.payload);
    },
  },
});

export const { listUser, addUser } = UserSlice.actions;

export default UserSlice.reducer;
