import { PayloadAction, createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import { User } from "../../../interface/Types";

//============ importamos el fetch del services=================
import fetchUser from "../../../services/fetchUser";

//creamos un thunk , podria estar en un archivo aparte
const fetchUsers = createAsyncThunk("users/fetchUser", async () => {
  const users = await fetchUser();
  return users;
});

let users = fetchUsers;

/**
 const users: User[] = [
  {
    id: 1,
    name: "John",
    lastName: "Doe",
  },
  {
    id: 2,
    name: "Jane",
    lastName: "Smith",
  },
  {
    id: 3,
    name: "Alice",
    lastName: "Johnson",
  },
  {
    id: 4,
    name: "Bob",
    lastName: "Brown",
  },
];
 */

const UserSlice = createSlice({
  name: "User",
  initialState: {
    users,
  },
  reducers: {
    setUser: (state) => {
      return users;
    },

    //
    addUser: (state, action: PayloadAction<User>) => {
      //console.log("action.types", action.type);
      // console.log(action.payload);
      // state.push(action.payload);
    },
  },
});

export const { listUser, addUser } = UserSlice.actions;

export default UserSlice.reducer;
