import React, { useState } from "react";

type Props = {};

const Contador = (props: Props) => {
  const [contador, SetContador] = useState(0);

  const handleIncrement = () => SetContador(contador + 1);

  return (
    <>
      <div className="" style={{ display: "flex", justifyContent: "center" }}>
        <h2 style={{ color: "blueviolet", margin: "10px 20px" }}>Contador</h2>
        <div style={{ display: "flex" }}>
          <button
            type="button"
            style={{
              backgroundColor: "cyan",
              padding: "4px 0",
              width: "60px",
              borderRadius: "15px",
              fontWeight: "bold",
              fontSize: "30px",
            }}
          >
            -
          </button>
          <span
            style={{
              padding: "4px 0",
              width: "60px",
              borderRadius: "15px",
              fontWeight: "bold",
              fontSize: "30px",
              textAlign: "center",
            }}
          >
            {contador}
          </span>
          <button
            type="button"
            style={{
              backgroundColor: "cyan",
              padding: "4px 0",
              width: "60px",
              borderRadius: "15px",
              fontWeight: "bold",
              fontSize: "30px",
            }}
            onClick={() => SetContador(contador + 1)}
          >
            +
          </button>
        </div>
      </div>
    </>
  );
};

export default Contador;
