import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { RootState } from "../Redux/store";
import {
  increment,
  decrement,
  incrementByAmount,
} from "../Redux/reducer/contador/CounterSlices";

import { CounterTypes } from "../Redux/Types";

import { Dispatch } from "@reduxjs/toolkit";
const Contador = () => {
  /**
   ==========metodo viejo cin redux con useSate=============
  const [contador, SetContador] = useState(0);
  const handleIncrement = () => SetContador(contador + 1);
 */

  //metodo con redux/slices
  const contador = useSelector((state: RootState) => state.counterSlices.count);

  //metodo con redux/reducer
  const contador2 = useSelector(
    (state: RootState) => state.counterReducer.count
  );

  const dispatch: Dispatch = useDispatch();

  return (
    <>
      <div className="" style={{ display: "flex", justifyContent: "center" }}>
        <h2 style={{ color: "blueviolet", margin: "10px 20px" }}>Contador</h2>
        <div style={{ display: "flex" }}>
          <button
            type="button"
            style={{
              backgroundColor: "cyan",
              padding: "4px 0",
              width: "60px",
              borderRadius: "15px",
              fontWeight: "bold",
              fontSize: "30px",
            }}
            //   onClick={() => SetContador(contador - 1)}
            //onClick={() => dispatch(decrement())}

            onClick={() =>
              dispatch({
                type: CounterTypes.DECREMENT,
              })
            }
          >
            -
          </button>
          <span
            style={{
              padding: "4px 0",
              width: "60px",
              borderRadius: "15px",
              fontWeight: "bold",
              fontSize: "30px",
              textAlign: "center",
            }}
          >
            {contador2}
          </span>
          <button
            type="button"
            style={{
              backgroundColor: "cyan",
              padding: "4px 0",
              width: "60px",
              borderRadius: "15px",
              fontWeight: "bold",
              fontSize: "30px",
            }}
            //onClick={() => SetContador(contador + 1)}
            //onClick={() => dispatch(increment())}
            onClick={() =>
              dispatch({
                type: CounterTypes.INCREMENT,
              })
            }
          >
            +
          </button>

          <button
            type="button"
            style={{
              backgroundColor: "cyan",
              padding: "4px 0",
              width: "200px",
              borderRadius: "15px",
              fontWeight: "bold",
              fontSize: "30px",
            }}
            //onClick={() => SetContador(contador + 1)}
            //  onClick={() => dispatch(incrementByAmount(2))}

            onClick={() =>
              dispatch({
                type: CounterTypes.INCREMENT_BY_AMOUNT,
                payload: 2,
              })
            }
          >
            increment x 2
          </button>
        </div>
      </div>
    </>
  );
};

export default Contador;
