import React from "react";

// en vez de usar esta forma de importar cada uno individual,use el hooks
//import { UseSelector, useDispatch, useSelector } from "react-redux";
//import { RootState } from "../Redux/store";

//=======================   usamos en hooks customizado======================================
import { useAppDispatch, useAppSelector } from "../hooks/useStore";

type Props = {};

const User = (props: Props): JSX.Element => {
  // const user = useSelector((state: RootState) => state.UserSlices);
  const user = useAppSelector((state) => state.UserSlices);

  //===============================================

  const handleChange = (e) => {
    const { name, value } = e.target;
  };
  const handleSubmit = () => {};
  //================================================

  return (
    <>
      <h2 style={{ display: "flex", justifyContent: "center" }}>
        Listado de Users
      </h2>

      {user &&
        user.map((u) => (
          <div
            key={u.id}
            style={{ display: "flex", gap: 2, justifyContent: "center" }}
          >
            <p>{u.name}</p>
            <p>{u.lastname}</p>
          </div>
        ))}

      {/*
================================================================================
*/}

      <div
        style={{
          display: "flex",
          justifyContent: "center",
          marginTop: "30px",
        }}
      >
        <form
          style={{
            display: "flex",
            flexDirection: "column",
            justifyContent: "center",
            marginTop: "30px",
            gap: 1,
          }}
          onSubmit={handleSubmit}
        >
          <h2>Formulario User</h2>
          <label htmlFor="name">Name</label>
          <input
            id="name"
            type="text"
            value=""
            style={{
              margin: "4px 0px",
              padding: "8px",
              borderRadius: "8px",

              fontWeight: "bold",
            }}
          />

          <label htmlFor="lastName">Last Name</label>
          <input
            id="lastName"
            type="text"
            value=""
            style={{
              margin: "4px 0px",
              padding: "8px",
              borderRadius: "8px",

              fontWeight: "bold",
            }}
          />

          <button
            type="submit"
            style={{
              margin: "4px 0px",
              padding: "8px",
              borderRadius: "8px",
              backgroundColor: "#14b41c",
              color: "white",
              fontWeight: "bold",
            }}
          >
            Enviar
          </button>
        </form>
      </div>
    </>
  );
};

export default User;
