import React from "react";

// en vez de usar esta forma de importar cada uno individual,use el hooks
//import { UseSelector, useDispatch, useSelector } from "react-redux";
//import { RootState } from "../Redux/store";

//=======================   usamos en hooks customizado======================================
import { useAppDispatch, useAppSelector } from "../hooks/useStore";

type Props = {};

const User = (props: Props) => {
  // const user = useSelector((state: RootState) => state.UserSlices);
  const user = useAppSelector((state) => state.UserSlices);
  return (
    <>
      <h2 style={{ display: "flex", justifyContent: "center" }}>
        Listado de Users
      </h2>

      {user &&
        user.map((u) => (
          <div
            key={u.id}
            style={{ display: "flex", gap: 2, justifyContent: "center" }}
          >
            <p>{u.name}</p>
            <p>{u.lastname}</p>
          </div>
        ))}

      <div
        style={{
          display: "flex",
          justifyContent: "center",
          marginTop: "30px",
        }}
      >
        <form
          action=""
          style={{
            display: "flex",
            flexDirection: "column",
            justifyContent: "center",
            marginTop: "30px",
            gap: 1,
          }}
        >
          <h2>Formulario User</h2>
          <label htmlFor="name">Name</label>
          <input id="name" type="text" value="" />

          <label htmlFor="lastName">Last Name</label>
          <input id="lastName" type="text" />

          <button
            type="button"
            style={{ margin: "8px 0px", padding: "5px", borderRadius: "8px" }}
          >
            Enviar
          </button>
        </form>
      </div>
    </>
  );
};

export default User;
