import { Action } from "@reduxjs/toolkit";
import { CounterTypes } from "../Types";

interface InitialStateCount {
  count: number;
}

const InitialState: InitialStateCount = {
  count: 1,
};

const reducerCounter = (state = InitialState, action: Action) => {
  switch (action.type) {
    case CounterTypes.INCREMENT:
      return { state: state.count + 1 };

    case CounterTypes.DECREMENT:
      return {
        state: state.count - 1,
      };

    default:
      return state;
  }
};

export default reducerCounter;

/*
interface IncrementAction extends Action {
  type: "increment";
}

interface DecrementAction extends Action {
  type: "decrement";
}

type CounterActionTypes = IncrementAction | DecrementAction;

*/
