import { PayloadAction, createSlice } from "@reduxjs/toolkit";

const users = [
  {
    id: 1,
    name: "John",
    lastname: "Doe",
  },
  {
    id: 2,
    name: "Jane",
    lastname: "Smith",
  },
  {
    id: 3,
    name: "Alice",
    lastname: "Johnson",
  },
  {
    id: 4,
    name: "Bob",
    lastname: "Brown",
  },
];
const UserSlice = createSlice({
  name: "User",
  initialState: users,
  reducers: {
    listUser: (state) => {
      return state;
    },
    addUser: (state, action: PayloadAction) => {
      //console.log("action.types", action.type);
      console.log(action.payload);
      return { state: action.payload };
    },
  },
});

export const { listUser, addUser } = UserSlice.actions;

export default UserSlice.reducer;
